  justify-content: center;
  align-items: center;
}

.popup.active {
  display: flex;
  opacity: 1;
}

.popup-content {
  background: white;
  width: 100%;
  max-width: 480px;
  border-radius: 20px;
  overflow: hidden;
  box-shadow: 0 25px 50px rgba(0, 0, 0, 0.25);
  transform: translateY(30px);
  transition: transform 0.4s cubic-bezier(0.175, 0.885, 0.32, 1.275);
}

.popup.active .popup-content {
  transform: translateY(0);
}

.popup-header {
  padding: 24px;
  background: linear-gradient(135deg, #6e45e2 0%, #88d3ce 100%);
  color: white;
  position: relative;
}

.popup-header h2 {
  font-size: 1.8rem;
  font-weight: 600;
  margin-bottom: 5px;
}

.close {
