  cursor: pointer;
  transition: transform 0.2s;
  width: 36px;
  height: 36px;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 50%;
}

.close:hover {
  transform: rotate(90deg);
  background: rgba(255, 255, 255, 0.2);
}

/* Стили формы */
