  padding: 30px;
  display: flex;
  flex-direction: column;
  gap: 20px;
}

.form-group {
  display: flex;
  flex-direction: column;
  gap: 8px;
}

label {
  font-weight: 500;
  color: #2d3748;
  font-size: 0.95rem;
}

input[type="tel"],
input[type="email"] {
  padding: 16px;
  border: 2px solid #e2e8f0;
  border-radius: 12px;
  font-size: 1rem;
  transition: all 0.3s ease;
}

input[type="tel"]:focus,
input[type="email"]:focus {
  border-color: #6e45e2;
  outline: none;
  box-shadow: 0 0 0 4px rgba(110, 69, 226, 0.1);
